    console.log("Effect ran")
    fetch("https://opentdb.com/api.php?amount=5")
      .then(res => res.json())
      .then(data => setTrivia(data.results))
  }, [])
  const questions = trivia.map((q)=>{
    const opt = q.incorrect_answers
